// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Components
import { Display, DISPLAY_TYPES } from "./Display";
import { DisplayContext, useDisplay } from "./DisplayContext";

export const DISPLAY_CONTAINER_ORIENTATION = {
    HORIZONTAL: "horizontal",
    VERTICAL: "vertical",
};

export function DisplayContainer({
    children,
    className,
    orientation = DISPLAY_CONTAINER_ORIENTATION.VERTICAL,
    displayType: displayTypeProp,
    ...otherProps
}) {
    const containerClassName = classnames(
        "brain-c-display-container",
        {
            "brain-c-display-container--horizontal": orientation === DISPLAY_CONTAINER_ORIENTATION.HORIZONTAL,
            "brain-c-display-container--vertical": orientation === DISPLAY_CONTAINER_ORIENTATION.VERTICAL,
        },
        className
    );

    const { type: displayTypeContext } = useDisplay();
    const displayType = displayTypeProp ?? displayTypeContext;

    return (
        <DisplayContext.Provider value={{ type: displayType }}>
            <div className={containerClassName} {...otherProps}>
                {children}
            </div>
        </DisplayContext.Provider>
    );
}

DisplayContainer.propTypes = {
    /**
     * Display for nested displays
     */
    displayType: PropTypes.oneOf(Object.values(DISPLAY_TYPES)),
    /**
     * Children displays
     */
    children: PropTypes.arrayOf(Display),
    /**
     * Additional className for container
     */
    className: PropTypes.string,
    /**
     * Orientation for container
     */
    orientation: PropTypes.oneOf(Object.values(DISPLAY_CONTAINER_ORIENTATION)),
};
