// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

export const CARD_CONTAINER_ORIENTATION = {
    HORIZONTAL: "horizontal",
    VERTICAL: "vertical",
};

export function CardContainer({
    children,
    className,
    orientation = CARD_CONTAINER_ORIENTATION.VERTICAL,
    ...otherProps
}) {
    const rootClassName = classnames(
        "brain-c-card-container",
        {
            "brain-c-card-container--horizontal": orientation === CARD_CONTAINER_ORIENTATION.HORIZONTAL,
            "brain-c-card-container--vertical": orientation === CARD_CONTAINER_ORIENTATION.VERTICAL,
        },
        className
    );

    return (
        <div {...otherProps} className={rootClassName}>
            {children}
        </div>
    );
}

CardContainer.propTypes = {
    /**
     * Children to display
     */
    children: PropTypes.node,
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * Orientation for container
     */
    orientation: PropTypes.oneOf(Object.values(CARD_CONTAINER_ORIENTATION)),
};
