// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

export function ListItem({ children, className, onClick, role = "button", ...otherProps }) {
    const itemClassName = classnames(
        "brain-c-list-item",
        {
            "brain-o-actionable": !!onClick,
        },
        className
    );
    const itemContainerClassName = "brain-c-list-item__container";

    return (
        <li {...otherProps} className={itemClassName}>
            {/* The <div> element has a child <role> attribute that allows keyboard interaction */}
            {/* eslint-disable-next-line jsx-a11y/no-static-element-interactions */}
            <div
                className={itemContainerClassName}
                onClick={onClick}
                onKeyPress={onClick}
                role={onClick ? role : undefined}
            >
                {children}
            </div>
        </li>
    );
}

ListItem.propTypes = {
    /**
     * Children
     */
    children: PropTypes.node,
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * Click handler
     */
    onClick: PropTypes.func,
    /**
     * Role for element
     */
    role: PropTypes.string,
};
