// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { wrapLabel } from "../../_utils";

// Components
import { IconWrapper, ICON_SIZE, ICON_POSITION } from "../icons";
import { Loader } from "../loaders";

export const BUTTON_TYPES = {
    PRIMARY: "primary",
    SECONDARY: "secondary",
    CAUTION: "caution",
};

export { ICON_SIZE as BUTTON_ICON_SIZE, ICON_POSITION as BUTTON_ICON_POSITION };

export const Button = React.forwardRef(function Button(
    {
        children,
        className,
        disabled: disabledProp = false,
        icon,
        iconClassName: iconClassNameProp,
        iconPosition = ICON_POSITION.LEFT,
        iconProps,
        iconSize = ICON_SIZE.M,
        isLoading = false,
        label,
        onClick,
        type = BUTTON_TYPES.PRIMARY,
        ...otherProps
    },
    ref
) {
    const disabled = disabledProp || isLoading;

    const buttonClassName = classnames(
        {
            "brain-theme--color": !disabled && [BUTTON_TYPES.PRIMARY, BUTTON_TYPES.CAUTION].includes(type),
        },
        "brain-o-actionable",
        "brain-c-button",
        {
            "brain-c-button--primary": type === BUTTON_TYPES.PRIMARY,
            "brain-c-button--secondary": type === BUTTON_TYPES.SECONDARY,
            "brain-c-button--caution": type === BUTTON_TYPES.CAUTION,
        },
        className
    );
    const iconClassName = classnames("brain-c-button__icon", iconClassNameProp);

    const wrappedChildren = wrapLabel(label ?? children);

    return (
        <button className={buttonClassName} disabled={disabled === true} onClick={onClick} ref={ref} {...otherProps}>
            <IconWrapper
                className={iconClassName}
                icon={isLoading ? <Loader /> : icon}
                position={iconPosition}
                size={iconSize}
            >
                {wrappedChildren}
            </IconWrapper>
        </button>
    );
});

Button.displayName = "Button";

Button.propTypes = {
    /**
     * Content of the button
     */
    children: PropTypes.node,
    /**
     * Additional className for the button
     */
    className: PropTypes.string,
    /**
     * If button is disabled
     */
    disabled: PropTypes.bool,
    /**
     * Icon displayed with the button
     */
    icon: IconWrapper.propTypes.icon,
    /**
     * Additional className for the icon
     */
    iconClassName: PropTypes.string,
    /**
     * Position of the icon
     */
    iconPosition: IconWrapper.propTypes.position,
    /**
     * Additional props for icon
     */
    iconProps: PropTypes.shape(IconWrapper.propTypes),
    /**
     * Size of the icon
     */
    iconSize: IconWrapper.propTypes.size,
    /**
     * If is loading
     */
    isLoading: PropTypes.bool,
    /**
     * Label of the button
     */
    label: PropTypes.string,
    /**
     * Click handler
     */
    onClick: PropTypes.func,
    /**
     * Type of button
     */
    type: PropTypes.oneOf(Object.values(BUTTON_TYPES)),
};
