// Libs
import React from "react";
import classnames from "classnames";

// Components
import { Icon, ICON_SIZE } from "../icons";
import { Button } from "./Button";

export const ButtonIcon = React.forwardRef(({ className, icon, children, ...otherProps }, ref) => {
    const buttonClassName = classnames("brain-c-button-icon", className);
    const child = icon ?? children;

    return (
        <Button {...otherProps} className={buttonClassName} ref={ref}>
            <Icon size={ICON_SIZE.M}>{child}</Icon>
        </Button>
    );
});

ButtonIcon.displayName = "ButtonIcon";

ButtonIcon.propTypes = {
    ...Button.propTypes,
};
