// Libs
import React, { useRef, useState } from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { wrapLabel } from "../../_utils";

// Components
import { Button, ButtonIcon, BUTTON_TYPES } from "../buttons";
import { IconWrapper, ICON_SIZE } from "../icons";
import { List, ListItem } from "../lists";
import { Dropdown } from "../modals";

function renderAction(
    actionButtonClassName,
    actionButtonProps,
    { action, buttonType = "standard", icon, key, label, type = BUTTON_TYPES.SECONDARY }
) {
    const ButtonComponent = buttonType === "icon" ? ButtonIcon : Button;

    return (
        <ButtonComponent
            {...actionButtonProps}
            className={actionButtonClassName}
            icon={icon}
            key={key ?? label}
            onClick={action}
            type={type}
        >
            {label}
        </ButtonComponent>
    );
}

function renderActionList(closeHandler, actionButtonClassName, actionButtonProps, { action, icon, key, label }) {
    const clickHandler = (event) => {
        closeHandler();
        action(event);
    };

    return (
        <ListItem {...actionButtonProps} className={actionButtonClassName} key={key ?? label} onClick={clickHandler}>
            <IconWrapper icon={icon} size={ICON_SIZE.S}>
                {wrapLabel(label)}
            </IconWrapper>
        </ListItem>
    );
}

export function Header({
    actions = [],
    actionButtonClassName: actionButtonClassNameProp,
    actionButtonProps,
    children,
    className,
    dropdownClassName: dropdownClassNameProp,
    dropdownProps,
    listClassName: listClassNameProp,
    listProps,
    moreButtonClassName: moreButtonClassNameProp,
    moreButtonProps,
    ...otherProps
}) {
    const anchorRef = useRef();
    const [open, setOpen] = useState(false);

    const actionButtonClassName = classnames("brain-c-header__action-button", actionButtonClassNameProp);
    const dropdownClassName = classnames("brain-c-header__dropdown", dropdownClassNameProp);
    const listClassName = classnames("brain-c-header__list", listClassNameProp);
    const moreButtonClassName = classnames("brain-c-header__more-button", moreButtonClassNameProp);
    const rootClassName = classnames("brain-o-paper brain-o-paper--rectangular", "brain-c-header", className);

    let actionNode;
    if (actions.length > 3) {
        actionNode = (
            <>
                {actions.slice(0, 2).map((action) => renderAction(actionButtonClassName, actionButtonProps, action))}
                <ButtonIcon
                    {...moreButtonProps}
                    className={moreButtonClassName}
                    onClick={() => setOpen(true)}
                    ref={anchorRef}
                    type={BUTTON_TYPES.SECONDARY}
                >
                    more-h
                </ButtonIcon>
                <Dropdown
                    {...dropdownProps}
                    anchorEl={anchorRef}
                    anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
                    className={dropdownClassName}
                    open={open}
                    onClose={() => setOpen(false)}
                    transformOrigin={{ vertical: "top", horizontal: "right" }}
                >
                    <List {...listProps} className={listClassName}>
                        {actions
                            .slice(2)
                            .map((action) =>
                                renderActionList(() => setOpen(false), actionButtonClassName, actionButtonProps, action)
                            )}
                    </List>
                </Dropdown>
            </>
        );
    } else {
        actionNode = actions.map((action) => renderAction(actionButtonClassName, actionButtonProps, action));
    }

    return (
        <div {...otherProps} className={rootClassName}>
            <div className="brain-c-header__content">{children}</div>
            <div className="brain-c-header__actions">{actionNode}</div>
        </div>
    );
}

Header.propTypes = {
    /**
     * Actions to display
     */
    actions: PropTypes.arrayOf(
        PropTypes.shape({
            /**
             * Click handler
             */
            action: PropTypes.func,
            /**
             * Type of button
             */
            buttonType: PropTypes.oneOf(["standard", "icon"]),
            /**
             * Icon for action
             */
            icon: PropTypes.string,
            /**
             * Key for react
             */
            key: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
            /**
             * Label for action
             */
            label: PropTypes.string,
            /**
             * Type of button
             */
            type: PropTypes.oneOf(Object.values(BUTTON_TYPES)),
        })
    ),
    /**
     * Additional className for action buttons
     */
    actionButtonClassName: PropTypes.string,
    /**
     * Additional props for action buttons
     */
    actionButtonProps: PropTypes.shape(Button.propTypes),
    /**
     * Children (left part)
     */
    children: PropTypes.node,
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * Additional className for Dropdown
     */
    dropdownClassName: PropTypes.string,
    /**
     * Additional props for Dropdown
     */
    // dropdownProps: PropTypes.shape(Dropdown.propTypes), It makes it crash...
    /**
     * Additional className for List
     */
    listClassName: PropTypes.string,
    /**
     * Additional prop for List
     */
    listProps: PropTypes.shape(List.propTypes),
    /**
     * Additional className for MoreButton
     */
    moreButtonClassName: PropTypes.string,
    /**
     * Additional props for MoreButton
     */
    moreButtonProps: PropTypes.shape(ButtonIcon.propTypes),
};
