import { useCallback, useEffect, useMemo, useRef, useState } from "react";

function setRef(ref, value) {
    if (typeof ref === "function") {
        ref(value);
    } else if (ref) {
        ref.current = value;
    }
}

/**
 * When you need to have a ref in to place, fork it
 * @param {React.MutableRefObject} refA
 * @param {React.MutableRefObject} refB
 */
export function useForkRef(refA, refB) {
    return useMemo(() => {
        if (refA === null && refB == null) {
            return null;
        }

        return (refValue) => {
            setRef(refA, refValue);
            setRef(refB, refValue);
        };
    }, [refA, refB]);
}

export function useFocus(disabled, onFocus) {
    const [focus, setFocus] = useState(false);

    const _handleFocus = useCallback(
        (event) => {
            if (disabled) {
                event.stopPropagation();
                return;
            }

            if (onFocus) {
                onFocus(event);
            }

            setFocus(true);
        },
        [disabled, onFocus, setFocus]
    );

    return { focus, setFocus, _handleFocus };
}

export function useFocusAndBlur(disabled, onFocus, onBlur) {
    const { focus, setFocus, _handleFocus } = useFocus(disabled, onFocus);

    const _handleBlur = useCallback(
        (event) => {
            if (disabled) {
                event.stopPropagation();
                return;
            }

            if (onBlur) {
                onBlur(event);
            }

            setFocus(false);
        },
        [disabled, onBlur, setFocus]
    );

    return { focus, setFocus, _handleFocus, _handleBlur };
}

export function useIsMounted() {
    const isMounted = useRef(false);

    useEffect(() => {
        isMounted.current = true;
    }, []);

    return isMounted;
}
