// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

export function LoaderProgression({ className, percent = 0 }) {
    const rootClassName = classnames("brain-c-loader-progression", className);

    return (
        <div className={rootClassName}>
            <div style={{ width: `${percent}%` }} className="brain-c-loader-progression__progress-bar" />
        </div>
    );
}

LoaderProgression.propTypes = {
    /**
     * Additional className for loader
     */
    className: PropTypes.string,
    /**
     * Percent of loading
     */
    percent: PropTypes.number,
};
