// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { alterElement } from "../../_utils";

// Components
import { Icon } from "../icons";
import { Tooltip } from "../modals";

export function CardTitle({
    children,
    className,
    helperIcon = "question-circle",
    helperIconProps,
    helperText,
    title,
    ...otherProps
}) {
    const titleClassName = classnames("brain-c-card__title", className);

    const wrappedChild = alterElement(title ?? children, { className: "brain-u-text--title" });

    return (
        <div className={titleClassName} {...otherProps}>
            {wrappedChild}
            {helperText && (
                <Tooltip tooltip={helperText}>
                    <Icon {...helperIconProps} className="brain-c-card__title-helper">
                        {helperIcon}
                    </Icon>
                </Tooltip>
            )}
        </div>
    );
}

CardTitle.propTypes = {
    /**
     * Content of title
     */
    children: PropTypes.node,
    /**
     * Additional className for title
     */
    className: PropTypes.string,
    /**
     * Helper icon to display
     */
    helperIcon: PropTypes.string,
    /**
     * Additional props for helper Icon
     */
    helperIconProps: PropTypes.shape(Icon.propTypes),
    /**
     * Helper text
     */
    helperText: PropTypes.string,
    /**
     * Title
     */
    title: PropTypes.string,
};
