// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { formatDateTime } from "../../_utils";

// Components
import { Card, CardTitle, CardSeparator, CardContent } from "../../molecules/cards";
import { DisplayResource, Display, DisplayContainer, DISPLAY_TYPES } from "../../molecules/displays";

export function InfoCard({ children, className, createdAt, owners: ownersProp, title = "Information", ...otherProps }) {
    const InfoCardClassName = classnames("brain-c-info-card", className);

    const owners = Array.isArray(ownersProp) ? ownersProp : [ownersProp];

    return (
        <Card {...otherProps} className={InfoCardClassName}>
            <CardTitle>{title}</CardTitle>
            <CardContent>
                <DisplayContainer className="brain-c-info-card__display-container" type={DISPLAY_TYPES.VERTICAL}>
                    <Display
                        label="Owner"
                        value={owners.map((owner) => (
                            <DisplayResource label={owner} icon="user" />
                        ))}
                    />
                    <Display label="Creation date" value={formatDateTime(createdAt)} />
                </DisplayContainer>
            </CardContent>
            {children && <CardSeparator />}
            {children}
        </Card>
    );
}

InfoCard.propTypes = {
    /**
     * Content of the button
     */
    children: PropTypes.node,
    /**
     * Additional className for the Card
     */
    className: PropTypes.string,
    /**
     * Date of the creation
     */
    createdAt: PropTypes.string,
    /**
     *  Owner of the resource
     */
    owners: PropTypes.oneOfType([PropTypes.string, PropTypes.arrayOf(PropTypes.string)]),
    /**
     * Title of the card ( information )
     */
    title: PropTypes.string,
};
