// Libs
import React, { useContext } from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { wrapLabel } from "../../_utils";

// Components
import { IconWrapper, ICON_POSITION } from "../icons";
import { DisplayContext } from "./DisplayContext";

export const DISPLAY_TYPES = {
    ROW: "row",
    COLUMN: "column",
};

const DISPLAY_TYPES_ICON_POSITION_MAPPER = {
    [DISPLAY_TYPES.ROW]: ICON_POSITION.RIGHT,
    [DISPLAY_TYPES.COLUMN]: ICON_POSITION.LEFT,
};

export function Display({
    children,
    className,
    icon,
    iconClassName: iconClassNameProp,
    iconProps,
    label,
    onClick,
    role = "button",
    tabIndex = 0,
    type: typeProp = DISPLAY_TYPES.ROW,
    value,
    ...otherProps
}) {
    const { type: typeContext } = useContext(DisplayContext);
    const type = typeContext ?? typeProp;

    const displayClassName = classnames(
        "brain-c-display",
        {
            "brain-o-actionable": !!onClick,
            "brain-c-display--horizontal": type === DISPLAY_TYPES.ROW,
            "brain-c-display--vertical": type === DISPLAY_TYPES.COLUMN,
        },
        className
    );
    const iconClassName = classnames("brain-c-display__icon brain-c-display__value", iconClassNameProp);

    const child = wrapLabel(value ?? children);

    return (
        /* The <div> element has a child <role> attribute that allows keyboard interaction */
        /* eslint-disable-next-line jsx-a11y/no-static-element-interactions */
        <div
            {...otherProps}
            className={displayClassName}
            onClick={onClick}
            onKeyPress={onClick}
            role={onClick ? role : undefined}
            tabIndex={onClick ? tabIndex : undefined}
        >
            <div className="brain-c-display__label brain-u-text--label">{label}</div>
            <IconWrapper
                {...iconProps}
                className={iconClassName}
                icon={icon}
                position={DISPLAY_TYPES_ICON_POSITION_MAPPER[type]}
            >
                {child}
            </IconWrapper>
        </div>
    );
}

Display.propTypes = {
    /**
     * Children to display
     */
    children: PropTypes.node,
    /**
     * Additional className for display
     */
    className: PropTypes.string,
    /**
     * Icon component
     */
    icon: IconWrapper.propTypes.icon,
    /**
     * Additional className for icon
     */
    iconClassName: PropTypes.string,
    /**
     * Additional props for icon
     */
    iconProps: PropTypes.shape(IconWrapper.propTypes),
    /**
     * Label
     */
    label: PropTypes.string.isRequired,
    /**
     * Click handler
     */
    onClick: PropTypes.func,
    /**
     * Role to the display
     */
    role: PropTypes.string,
    /**
     * Tab index value
     */
    tabIndex: PropTypes.number,
    /**
     * Type of display
     */
    type: PropTypes.oneOf(Object.values(DISPLAY_TYPES)),
    /**
     * Value
     */
    value: PropTypes.string,
};
