// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

import { Icon, ICON_SIZE } from "./Icon";

export const ICON_POSITION = {
    TOP: "top",
    RIGHT: "right",
    BOTTOM: "bottom",
    LEFT: "left",
};

export function IconWrapper({
    children,
    className,
    icon,
    iconProps = {},
    onClick,
    position = ICON_POSITION.LEFT,
    size,
    ...otherProps
}) {
    const rootClassName = classnames(
        "brain-c-icon-wrapper",
        {
            "brain-c-icon-wrapper--top": ICON_POSITION.TOP === position,
            "brain-c-icon-wrapper--right": ICON_POSITION.RIGHT === position,
            "brain-c-icon-wrapper--bottom": ICON_POSITION.BOTTOM === position,
            "brain-c-icon-wrapper--left": ICON_POSITION.LEFT === position,
        },
        className
    );
    const iconClassName = classnames("brain-c-icon-wrapper__icon", iconProps.className);

    return (
        <div {...otherProps} className={rootClassName}>
            {children}
            {icon && (
                <Icon {...iconProps} className={iconClassName} onClick={onClick} size={size}>
                    {icon}
                </Icon>
            )}
        </div>
    );
}

IconWrapper.propTypes = {
    /**
     * Children to display
     */
    children: PropTypes.node,
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * Icon
     */
    icon: PropTypes.oneOfType([PropTypes.element, PropTypes.string]),
    /**
     * Additional props for Icon
     */
    iconProp: PropTypes.shape(Icon.propTypes),
    /**
     * Click handler
     */
    onClick: PropTypes.func,
    /**
     * Position for icon
     */
    position: PropTypes.oneOf(Object.values(ICON_POSITION)),
    /**
     * Size for icon
     */
    size: PropTypes.oneOf(Object.values(ICON_SIZE)),
};
