// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Utils
import { wrapLabel } from "../../_utils";

// Components
import { IconWrapper, ICON_POSITION } from "../icons";

export const DISPLAY_RESOURCE_STATUS = {
    INFO: "info",
    SUCCESS: "success",
    WARNING: "warning",
    ERROR: "error",
};

export function DisplayResource({ className, disabled = false, helper, icon, label, status, ...otherProps }) {
    const rootClassName = classnames(
        "brain-c-display-resource",
        {
            [`brain-u-status--${status}`]: !disabled && status,
        },
        className
    );
    return (
        <div {...otherProps} className={rootClassName} disabled={disabled === true}>
            <IconWrapper icon={icon} position={ICON_POSITION.LEFT}>
                <div>
                    {label && <div className="brain-c-display-resource__label">{wrapLabel(label)}</div>}
                    {helper && <div className="brain-c-display-resource__helper">{wrapLabel(helper)}</div>}
                </div>
            </IconWrapper>
        </div>
    );
}

DisplayResource.propTypes = {
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * If is disabled
     */
    disabled: PropTypes.bool,
    /**
     * Helper text
     */
    helper: PropTypes.string,
    /**
     * Icon
     */
    icon: IconWrapper.propTypes.icon,
    /**
     * Label
     */
    label: PropTypes.string.isRequired,
    /**
     * Optional status
     */
    status: PropTypes.oneOf(Object.values(DISPLAY_RESOURCE_STATUS)),
};
