// Libs
import React, { useState } from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'

// Utils
import { formatDate, formatTime } from '../../_utils'

// Components
import { ButtonIcon, ButtonAngle } from '../../molecules/buttons'
import {
  Card,
  CardTitle,
  CardContent,
  CardSeparator,
} from '../../molecules/cards'
import { Display, DisplayContainer } from '../../molecules/displays'
import { List, ListItem } from '../../molecules/lists'

export function ResourceCard({
  actionAt,
  actions = [],
  actionType,
  className,
  owners: ownersProp,
  properties,
  title,
  ...otherProps
}) {
  const [isActionOpen, setActionOpen] = useState(false)

  const resourceCardClassName = classnames('brain-c-resource-card', className)

  const owners = Array.isArray(ownersProp) ? ownersProp : [ownersProp]

  return (
    <Card {...otherProps} className={resourceCardClassName}>
      <div className="brain-c-resource-card__more-button">
        <ButtonAngle onClick={() => setActionOpen(true)}>more-h</ButtonAngle>
      </div>
      {isActionOpen && (
        <div className="brain-theme--color brain-c-resource-card__action-menu">
          <div className="brain-c-resource-card__button-container">
            <ButtonIcon
              className="brain-c-resource-card__close-button"
              onClick={() => setActionOpen(false)}
              type="secondary"
            >
              close
            </ButtonIcon>
          </div>
          <List>
            {actions.map((action) => (
              <ListItem
                key={action.key ?? action.label}
                onClick={action.onClick}
              >
                {action.label}
              </ListItem>
            ))}
          </List>
        </div>
      )}
      <CardTitle>{title}</CardTitle>
      <CardContent className="brain-c-resource-card__secondary-text">
        <div>{`Owned by ${owners.join(', ')}`}</div>
        <div>{`${actionType} on ${formatDate(actionAt)}, ${formatTime(
          actionAt
        )}`}</div>
      </CardContent>
      <CardSeparator />
      <CardContent>
        <DisplayContainer type="horizontal">
          {properties.map((p) => (
            <Display
              key={p.key ?? p.label}
              label={p.label}
              value={p.value}
              icon={p.icon}
            />
          ))}
        </DisplayContainer>
      </CardContent>
    </Card>
  )
}

ResourceCard.propTypes = {
  /**
   * Date of action
   */
  actionAt: PropTypes.string.isRequired,
  /**
   * Secondary actions
   */
  actions: PropTypes.arrayOf(
    PropTypes.shape({
      /**
       * If is disabled
       */
      disabled: PropTypes.bool,
      /**
       * Key for react
       */
      key: PropTypes.string,
      /**
       * Action label
       */
      label: PropTypes.string.isRequired,
      /**
       * Action handler
       */
      onClick: PropTypes.func.isRequired,
    }).isRequired
  ),
  /**
   * Action type
   */
  actionType: PropTypes.string.isRequired,
  /**
   * Additional className
   */
  className: PropTypes.string,
  /**
   * List of owners for resource
   */
  owners: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.arrayOf(PropTypes.string),
  ]),
  /**
   * Resource properties
   */
  properties: PropTypes.arrayOf(
    PropTypes.shape({
      /**
       * Key for react
       */
      key: PropTypes.string,
      /**
       * Label for property
       */
      label: PropTypes.string.isRequired,
      /**
       * Value for property
       */
      value: PropTypes.string.isRequired,
      /**
       * Icon for property
       */
      icon: PropTypes.string,
    }).isRequired
  ),
  /**
   * Title for property
   */
  title: PropTypes.string.isRequired,
}
