// Libs
import React from "react";
import PropTypes from "prop-types";
import classnames from "classnames";

// Components
import { IconWrapper } from "../icons";

export const DISPLAY_STATUS = {
    DRAFT: "draft",
    IN_PROGRESS: "inProgress",
    PAUSED: "paused",
    COMPLETE: "complete",
    FAILED: "failed",
};

const TRANSLATIONS = {
    [DISPLAY_STATUS.DRAFT]: "Draft",
    [DISPLAY_STATUS.IN_PROGRESS]: "In Progress",
    [DISPLAY_STATUS.PAUSED]: "Paused",
    [DISPLAY_STATUS.COMPLETE]: "Complete !",
    [DISPLAY_STATUS.FAILED]: "Failed",
};

const DISPLAY_ICONS = {
    [DISPLAY_STATUS.DRAFT]: "clipboard",
    [DISPLAY_STATUS.IN_PROGRESS]: "hourglass",
    [DISPLAY_STATUS.PAUSED]: "pause",
    [DISPLAY_STATUS.COMPLETE]: "success",
    [DISPLAY_STATUS.FAILED]: "error",
};

export function DisplayStatus({ className, iconClassName: iconClassNameProp, iconProps, status, ...otherProps }) {
    const rootClassName = classnames(
        "brain-c-display-status",
        {
            "brain-c-display-status--draft": status === DISPLAY_STATUS.DRAFT,
            "brain-c-display-status--inProgress": status === DISPLAY_STATUS.IN_PROGRESS,
            "brain-c-display-status--paused": status === DISPLAY_STATUS.PAUSED,
            "brain-c-display-status--complete": status === DISPLAY_STATUS.COMPLETE,
            "brain-c-display-status--failed": status === DISPLAY_STATUS.FAILED,
        },
        className
    );

    const iconClassName = classnames("brain-c-display-status__icon", iconClassNameProp);

    if (!DISPLAY_ICONS[status]) {
        return <span className="brain-u-status--error">Unknown status</span>;   
    }

    return (
        <div {...otherProps} className={rootClassName}>
            <IconWrapper {...iconProps} className={iconClassName} icon={DISPLAY_ICONS[status]}>
                <span>{TRANSLATIONS[status]}</span>
            </IconWrapper>
        </div>
    );
}

DisplayStatus.propTypes = {
    /**
     * Additional className
     */
    className: PropTypes.string,
    /**
     * Additional className for Icon
     */
    iconClassName: PropTypes.string,
    /**
     * Additional props for Icon
     */
    iconProps: PropTypes.shape(IconWrapper.propTypes),
    /**
     * Status
     */
    status: PropTypes.oneOf(Object.values(DISPLAY_STATUS)),
};
